import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Donnation } from 'src/app/entities/donnation';
import { DonnationService } from 'src/app/services/donnation.service';

@Component({
  selector: 'app-donnation-list',
  templateUrl: './donnation-list.component.html',
  styleUrls: ['./donnation-list.component.css']
})
export class DonnationListComponent implements OnInit{

  currentCategoryId: number = 0 ;

  donnations: Donnation[] = [];

  searchMode: boolean=false;
  constructor(private donnationService: DonnationService,
              private route: ActivatedRoute){}

  ngOnInit(): void {
    this.route.paramMap.subscribe(
      () => {
              this.listDonnations()
            });
  }

  listDonnations(){
    
    this.searchMode = this.route.snapshot.paramMap.has('keyword');

    if(this.searchMode){
      const theKeyword: string = this.route.snapshot.paramMap.get('keyword')!;
      
      this.donnationService.searchDonnations(theKeyword).subscribe(
        data => {
          this.donnations = data;
        }
      )
      this.searchMode=false;
    }
    else{
      this.handleListDonnations();
    }
  }

  handleListDonnations(){
    //verifier si "id" est existant
    const hasCategoryId: boolean = this.route.snapshot.paramMap.has('id');
    if(hasCategoryId){

      //get the id param
      this.currentCategoryId=+this.route.snapshot.paramMap.get('id')!;
    }else{
      this.currentCategoryId = 0;
    }
     
    this.donnationService.getDonnationList(this.currentCategoryId).subscribe(
      data =>{
        this.donnations = data;
      }
    );
  }
}
<header class="grid place-content-center">
    <h2  class="text-gray-700 text-6xl font-semibold">Donations</h2>
    <h3  class="text-2xl font-semibold">For Charity Associations</h3>
</header>


    <h4 class="font-bold mt-12 pb-2 border-b border-gray-200">Latest Donations Announcement</h4>

<div class=" grid md:grid-cols-2 lg:grid-cols-3 gap-10"> 
    <div *ngFor="let TempDonnation of donnations">
        <div class="mt-8">
         <!-- Cards go here -->
            <div class="card hover:shadow-lg">
                <a  routerLink="/donnations/{{TempDonnation.id}}">
                <img src="assets/images/placeholder.jpg" class="w-full h-32 sm:h-48 object-cover">
                <div class="m-4">
                    <span class="font-bold">{{TempDonnation.name}}</span>
                    <span class="block text-gray-500">{{TempDonnation.description}}</span>
                </div>
                <div class="badge">
                    <svg class="w-5 inline-block mr-1" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M12 6v6h4.5m4.5 0a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                  
                    <span>{{TempDonnation.dateCreated}}</span>
                </div>
                </a>
            </div>
        </div>

    </div>

    <div *ngIf="donnations?.length==0" class="bg-orange-100 border-l-4 border-orange-500 text-orange-700 p-4 md:col-span-2 lg:col-span-3" role="alert">
        <p class="font-bold">Not Found</p>
        <p>We're sorry but the informations aren't disponible.</p>
      </div>

</div>

<div class="flex justify-center">
    <div class="btn text-gray-600 bg-gray-200 hover:shadow-inner transform hover:scale-125 hover:mt-1 hover:bg-opacity-50 transition ease-out duration-300 mb-3 mt-9">Load More</div>
</div>
